from flask import Flask, render_templateapp = Flask(__name__)posts = [    {'author': 'Kooks LOR',     'title': 'Blog Post 1',     'content': 'First post placeholder',     'date_posted': 'May 08, 2019',     },    {'author': 'Ce man-là qu''est un peu stylé',     'title': 'Blog Post #2',     'content': 'Second post grafiti attempt',     'date_posted': 'June 09, 2019',     },]@app.route("/")@app.route("/home")def home():    return render_template('home.html', posts=posts)@app.route("/about")def about():    return render_template('about.html', title='About')if __name__ == '__main__':    app.run(debug=True)